import React, { Component } from 'react';

// reactstrap components
import {
  Button,
  Container,
  Row,
  Col,
  UncontrolledPopover,
  PopoverBody,
  PopoverHeader,
} from 'reactstrap';

export default class ButtonPopover extends Component {
  render() {
    return (
      <div>
        <Button color='default' id={this.props.ButtonPopoverID} size='sm'>
          {this.props.title}
        </Button>
        <UncontrolledPopover
          trigger='focus'
          placement='top'
          target={this.props.ButtonPopoverID}
        >
          <PopoverHeader>Popover on Top</PopoverHeader>
          <PopoverBody>
            Vivamus sagittis lacus vel augue laoreet rutrum faucibus.
          </PopoverBody>
        </UncontrolledPopover>
      </div>
    );
  }
}
