import React, { Component } from 'react';
// nodejs library that concatenates classes
import classnames from 'classnames';

// reactstrap components
import { Button, Container, Row, Col } from 'reactstrap';

// core components
import DemoNavbar from 'components/Navbars/DemoNavbar.js';
import CardsFooter from 'components/Footers/CardsFooter.js';

// index page sections
import Download from '../IndexSections/Download.js';
import Hero from '../IndexSections/Hero';

import { connect } from 'react-redux';
import * as actions from '../../actions';
import { completeLanding } from '../../actions';
import _ from 'lodash';
import Carousel from '../../views/IndexSections/Carousel';
import SubCarousel from 'views/IndexSections/SubCarousel.js';
import ButtonPopover from '../../components/ButtonPopover';

class Landing extends Component {
  state = {};

  componentDidMount() {
    document.documentElement.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
  }
  componentWillMount() {
    this.props.fetchLanding();
  }

  contestItems = [
    {
      src: require('assets/img/theme/img-2-1200x1000.jpg'),
      altText: '',
      caption: '',
      header: '',
    },
  ];

  render() {
    const { data } = this.props;
    const getData = _.map(data, (value, key) => {
      console.log(value.title);
      return value.title;
    });
    return (
      <div>
        <DemoNavbar />
        <Hero title='' />
        <Carousel
          title='공모자들'
          subtitle='무슨 동아리인가요?'
          text1='춘천 유일의 공모전 연합동아리 입니다.
          ‘공모자들’에서는 경험이 없어도, 잘 하지 못해도 누구든 다양한
          사람들과 함께 공모전에 도전할 수 있습니다👍'
        />
        <Container>
          <h1>공모자들은 뭘 하나요?</h1>
          <Row>
            <Col>
              <SubCarousel category='공모전' items={contestItems} />
              <Row>
                <ButtonPopover
                  title='자유기획'
                  PH_text='자유기획'
                  PB_text='문화/예술, 교육, 봉사, 여행 등 모든 분야에 대해 생각합니다.'
                  PopID='B1'
                />
                &emsp;
                <ButtonPopover
                  title='팀프로젝트'
                  PH_text='팀프로젝트'
                  PB_text='매주 정기모임을 통해 서로의 생각을 공유합니다.'
                  PopID='B2'
                />
              </Row>
            </Col>
            <Col>
              <SubCarousel category='수상/경험' />
              <Row>
                <ButtonPopover
                  title='수상'
                  PH_text='수상'
                  PB_text='공모자들 모두의 도움으로 공모전 수상을 해봐요.'
                  PopID='C1'
                  color='success'
                />
                &emsp;
                <ButtonPopover
                  title='경험'
                  PH_text='경험'
                  PB_text='공모전을 준비하며 많은 생각을 해보는 경험을 해봐요.'
                  PopID='C2'
                  color='success'
                />
              </Row>
            </Col>
            <Col>
              <SubCarousel category='친목활동' />
              <Row>
                <ButtonPopover
                  title='친목활동'
                  PH_text='친목활동'
                  PB_text='영화, 야유회, 체험 등 공모자들 여러분과 친목을 다져봐요.'
                  PopID='D1'
                  color='warning'
                />
              </Row>
            </Col>
          </Row>
        </Container>
        <CardsFooter />
      </div>
    );
  }
}
